# Authentication
def registration(request):
  if request.method == 'POST':
    form = RegistrationForm(request.POST)
    if form.is_valid():
      form.save()
      print('Account created successfully!')
      return redirect('/admin/login/')
    else:
      print("Registration failed!")
  else:
    form = RegistrationForm()
  
  context = {'form': form}
  return render(request, 'admin/register.html', context)

class UserLoginView(auth_views.LoginView):
  template_name = 'admin/login.html'
  form_class = LoginForm
  success_url = '/'

class UserPasswordResetView(auth_views.PasswordResetView):
  template_name = 'accounts/password_reset.html'
  form_class = UserPasswordResetForm

class UserPasswordResetConfirmView(auth_views.PasswordResetConfirmView):
  template_name = 'accounts/password_reset_confirm.html'
  form_class = UserSetPasswordForm

class UserPasswordChangeView(auth_views.PasswordChangeView):
  template_name = 'accounts/password_change.html'
  form_class = UserPasswordChangeForm

def user_logout_view(request):
  logout(request)
  return redirect('/admin/login/')

from django.shortcuts import render
from django.contrib.auth.decorators import user_passes_test

def is_technician_or_superuser(user):
    return user.is_superuser or user.groups.filter(name='Tecnico').exists()

@user_passes_test(is_technician_or_superuser)
def some_view(request):
    # Definir show_ficha_operativa para el contexto
    show_ficha_operativa = request.user.is_superuser or request.user.groups.filter(name='Tecnico').exists()

    context = {
        'show_ficha_operativa': show_ficha_operativa,
        # otros contextos que desees pasar
    }
    return render(request, 'admin/fichas.html', context)
